import React, {useEffect, useState} from 'react';
import {NavLink, useParams,useHistory} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

import { userProfile } from '../../store/user';
import {deleteRsvp} from '../../store/rsvp';


function LoggedUserProfile({sessionUser}) {
  const user = useSelector(state => state.users);


  const [loading , setLoading] = useState(false);

  const dispatch = useDispatch();

  const {userId} = useParams();

  useEffect(() => {
    dispatch(userProfile(userId));
  }, [dispatch]);


  function sliceTimeOffDate(){
    let slicedDate = user.events.event_date.slice(0,10);
    return slicedDate;
  }


  // load the events the user is hosting and the events the user is attending so they can be displayed on the page separately
  function loadEvents(){
    let hostingEvents = [];
    let attendingEvents = [];
    if(user.events){
      for(let i = 0; i < user.events.length; i++){
        if(user.events[i].host_id === user.id){
          hostingEvents.push(user.events[i]);
        }else{
          attendingEvents.push(user.events[i]);
        }
      }
    }
    return [hostingEvents, attendingEvents];
  }



  return (
    <>
      <div className="logged-user-profile-container">
        <div className="logged-user-profile-header">
          <div className="logged-user-profile-header-left">
            <div className="logged-user-profile-header-left-img">
              <img src={user.profile_img} alt="user profile image" />
            </div>
            <div className="logged-user-profile-header-left-info">
              <h1>{sessionUser.username}</h1>
              <h2>{user.email}</h2>
            </div>
          </div>
          <div className="logged-user-profile-header-right">
            <div className="logged-user-profile-header-right-edit">
              <NavLink to={`/users/${user.id}/edit`}>Edit Profile</NavLink>
            </div>
          </div>
        </div>
        <div className="logged-user-profile-body">
          <div className="logged-user-profile-body-hosting">
            <h2>Hosting</h2>
            <div className="logged-user-profile-body-hosting-events">
              {loadEvents()[0] && loadEvents()[0].map((event) => {
                return (
                  <div className="logged-user-profile-body-hosting-events-event" key={event.id}>
                    <div className="logged-user-profile-body-hosting-events-event-left">
                      <div className="logged-user-profile-body-hosting-events-event-left-img">
                        <img src={event.event_img} alt="event image" />
                      </div>
                      <div className="logged-user-profile-body-hosting-events-event-left-info">
                        <h3>{event.event_name}</h3>
                        <h4>{sliceTimeOffDate(event.event_date)}</h4>
                      </div>
                    </div>
                    <div className="logged-user-profile-body-hosting-events-event-right">
                      <div className="logged-user-profile-body-hosting-events-event-right-edit">
                        <NavLink to={`/events/${event.id}/edit`}>Edit</NavLink>
                      </div>
                    </div>
                  </div>
                )
              })}
            </div>
          </div>
          <div className="logged-user-profile-body-attending">
            <h2>Attending</h2>
            <div className="logged-user-profile-body-attending-events">
              {loadEvents()[1] && loadEvents()[1].map((event) => {
                return (
                  <div className="logged-user-profile-body-attending-events-event" key={event.id}>
                    <div className="logged-user-profile-body-attending-events-event-left">
                      <div className="logged-user-profile-body-attending-events-event-left-img">
                        <img src={event.event_img} alt="event image" />
                      </div>
                      <div className="logged-user-profile-body-attending-events-event-left-info">
                        <h3>{event.event_name}</h3>
                        <h4>{sliceTimeOffDate(event.event_date)}</h4>
                      </div>
                    </div>
                    <div className="logged-user-profile-body-attending-events-event-right">
                      <div className="logged-user-profile-body-attending-events-event-right-edit">
                        <button onClick={() => dispatch(deleteRsvp(event.id))}>Cancel</button>
                      </div>
                    </div>
                  </div>
                )
              })}
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default LoggedUserProfile
