import React, {useState} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {useHistory} from 'react-router-dom'



const AddEvent = () => {
    const history = useHistory(); // so that we can redirect after the image upload is successful
    const user = useSelector(state => state.session)
    const dispatch = useDispatch()

    let today = new Date().toISOString().substring(0, 10);

    const [event_imgs, setImage] = useState("");
    const [imageLoading, setImageLoading] = useState(false);
    const [title, setTitle] = useState("")
    const [event_description, setDescription] = useState("")
    const [event_date, setDate] = useState("")
    const [event_city, setCity] = useState("")
    const [event_state, setState] = useState("")
    const [event_zipcode, setZip] = useState("")
    const [category, setCategory] = useState("")
    const [errors, setErrors] = useState([])


    const handleSubmit = async (e) => {
      e.preventDefault();
      const formData = new FormData();
      formData.append("event_imgs", event_imgs);
      formData.append("title", title);
      formData.append("event_description", event_description);
      formData.append("event_date", event_date);
      formData.append("event_city", event_city);
      formData.append("event_state", event_state);
      formData.append("event_zipcode", event_zipcode);
      formData.append("category", category);
      formData.append("user_id", user.id);


      // aws uploads can be a bit slow—displaying
      // some sort of loading message is a good idea

      setImageLoading(true);
      const res = await fetch('/api/events/new', {
          method: "POST",
          body: formData,
      });
      if (res.ok) {
          await res.json();
          setImageLoading(false);
          history.push("/");
      }
      else {
          setImageLoading(false);
          console.log("error")
      }
  }




    const updateImage = (e) => {
        const file = e.target.files[0];
        setImage(file);
    }

    return (
      <>
        <h1>Post a new Event</h1>
        <form onSubmit={handleSubmit}>
          <label>Add a photo</label>
            <input
              type="file"
              accept="image/*"
              onChange={updateImage}
            />
            <div>
              <label>Title</label>
              <input
                type='text'
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Description</label>
              <input
                type='text'
                value={event_description}
                onChange={(e) => setDescription(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Date</label>
              <input
                type="date"
                format="YYYY-MM-DD"
                value={event_date}
                onChange={(e) => setDate(e.target.value)}
                required
              />
            </div>
            <div>
              <label>City</label>
              <input
                type='text'
                value={event_city}
                onChange={(e) => setCity(e.target.value)}
                required
              />
            </div>
            <div>
              <label>State</label>
              <input
                type='text'
                value={event_state}
                onChange={(e) => setState(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Zip Code</label>
              <input
                type='text'
                value={event_zipcode}
                onChange={(e) => setZip(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Category</label>
              <select
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                required
              >
                <option value=''>--Please choose an option--</option>
                <option value='Sports'>Sports</option>
                <option value='VideoGames'>Video Games</option>
                <option value='Clubbing'>Clubbing</option>
                <option value='Boating'>Boating</option>
                <option value='BoardGames'>Board Games</option>
                <option value='StudyGroups'>Study Groups</option>
                <option value='Other'>Other</option>
              </select>
            </div>
            <button type="submit">Submit</button>
            {(imageLoading)&& <p>Loading...</p>}
        </form>
        </>
    )
}

export default AddEvent;


