import * as React from 'react';
import { useState } from 'react';
import {useDispatch, useSelector} from 'react-redux'
import {useHistory} from 'react-router-dom'


import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import NativeSelect from '@mui/material/NativeSelect';


import './addEvent.css'

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid black',
  boxShadow: 24,
  p: 4,

  input:{
    width: '100%',
    height: '30px',
    marginBottom: '10px',
    borderRadius: '5px',
    border: '1px solid black',
    padding: '5px',
},
  label:{
    display: 'flex',
    marginBottom: '5px',
    fontSize: '14px',
    fontWeight: 'bold',
    justifyContent: 'center',
    alignItems: 'center',
    fontFamily: 'Roboto',
},

}
window.addEventListener('DOMContentLoaded', function() {
	// IMPORTANT: Fill in your client key
	var clientKey = "FILL_IN_CLIENT_KEY";

	var cache = {};
	var container = $("#example1");
	var errorElem = container.find(".label-error");

	/** Handle successful response */
	function handleResp(data)
	{
		// Check for error
		if (data.error_msg)
			errorElem.text(data.error_msg);
		else if ("city" in data)
		{
			// Set city and state
			container.find("input[name='city']").val(data.city);
			container.find("input[name='state']").val(data.state);
		}
	}

	// Set up event handlers
	container.find("input[name='zipcode']").on("keyup change", function() {
		// Get zip code
		var zipcode = $(this).val().substring(0, 5);
		if (zipcode.length == 5 && /^[0-9]+$/.test(zipcode))
		{
			// Clear error
			errorElem.empty();

			// Check cache
			if (zipcode in cache)
			{
				handleResp(cache[zipcode]);
			}
			else
			{
				// Build url
				var url = "https://www.zipcodeapi.com/rest/"+clientKey+"/info.json/" + zipcode + "/radians";

				// Make AJAX request
				$.ajax({
					"url": url,
					"dataType": "json"
				}).done(function(data) {
					handleResp(data);

					// Store in cache
					cache[zipcode] = data;
				}).fail(function(data) {
					if (data.responseText && (json = $.parseJSON(data.responseText)))
					{
						// Store in cache
						cache[zipcode] = json;

						// Check for error
						if (json.error_msg)
							errorElem.text(json.error_msg);
					}
					else
						errorElem.text('Request failed.');
				});
			}
		}
	}).trigger("change");
});
export default function AddEvent() {
  let today = new Date().toISOString().substring(0, 10);

  const [open, setOpen] = useState(false);
  const [event_imgs, setImage] = useState("");
  const [imageLoading, setImageLoading] = useState(false);
  const [title, setTitle] = useState("")
  const [event_description, setDescription] = useState("")
  const [event_date, setDate] = useState("")
  const [event_city, setCity] = useState("")
  const [event_state, setState] = useState("")
  const [event_zipcode, setZip] = useState("")
  const [category, setCategory] = useState("")
  const [errors, setErrors] = useState([])

  const history = useHistory();
  const user = useSelector(state => state.session)
  const dispatch = useDispatch()

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("event_imgs", event_imgs);
    formData.append("title", title);
    formData.append("event_description", event_description);
    formData.append("event_date", event_date);
    formData.append("event_city", event_city);
    formData.append("event_state", event_state);
    formData.append("event_zipcode", event_zipcode);
    formData.append("category", category);
    formData.append("user_id", user.id);


    // aws uploads can be a bit slow—displaying
    // some sort of loading message is a good idea

    setImageLoading(true);
    const res = await fetch('/api/events/new', {
        method: "POST",
        body: formData,
    });
    if (res.ok) {
        await res.json();
        setImageLoading(false);
        history.push("/feed");
    }
    else {
        setImageLoading(false);
        console.log("error")
    }

    window.location.reload();
}




  const updateImage = (e) => {
      const file = e.target.files[0];
      setImage(file);
  }

  return (
    <div>
      <i className="fas fa-upload"  onClick={handleOpen}></i>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h6" component="h2">
            Create an Event
          </Typography>
          <Typography id="modal-modal-description" sx={{ mt: 2 }}>

          <form onSubmit={handleSubmit}>
          <label className="modal-label">Add a photo</label>
            <input
              type="file"
              accept="image/*"
              onChange={updateImage}
              required
            />
            <div>
              <label className="modal-label">Title</label>
              <input
                type='text'
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </div>
            <div>
              <label className="modal-label">Description</label>
              <input
                type='text'
                value={event_description}
                onChange={(e) => setDescription(e.target.value)}
                required
              />
            </div>
            <div>
              <label className="modal-label">Date</label>
              <input
                type="date"
                format="YYYY-MM-DD"
                value={event_date}
                onChange={(e) => setDate(e.target.value)}
                min={today}
                required
              />
            </div>
            <div>
              <label className="modal-label">City</label>
              <input
                type='text'
                value={event_city}
                onChange={(e) => setCity(e.target.value)}
                required
              />
            </div>
            <div>
              <label className="modal-label">State</label>
              <input
                type='text'
                value={event_state}
                onChange={(e) => setState(e.target.value)}
                required
              />
            </div>
            <div>
              <label className="modal-label">Zip Code</label>
              <input
                type='text'
                value={event_zipcode}
                onChange={(e) => setZip(e.target.value)}
                required
              />
            </div>
      <Box sx={{ minWidth: 120 }}>
        <FormControl fullWidth>
            <InputLabel variant="standard" htmlFor="uncontrolled-native" >
              Select a category
            </InputLabel>
            <NativeSelect
              defaultValue={'Sports'}
              inputProps={{
                name: 'category',
                id: 'uncontrolled-native',
              }}
            >
              <option value={'Sports'}>Sports</option>
              <option value={'VideoGames'}>Video games</option>
              <option value={'Clubbing'}>Clubbing</option>
              <option value={'Boating'}>Boating</option>
              <option value={'BoardGames'}>Board Games</option>
              <option value={'StudyGroups'}>Study Groups</option>
              <option value={'Other'}>Other</option>
            </NativeSelect>
        </FormControl>
    </Box>
            <button type="submit" className="modal-submit">Submit</button>
            {(imageLoading)&& <p>Loading...</p>}
        </form>
          </Typography>
        </Box>
      </Modal>
    </div>
  );
}

