import React, {useEffect, useState} from 'react';
import {NavLink, useParams,useHistory} from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

import { userProfile } from '../../store/user';
import {deleteRsvp} from '../../store/rsvp';


function LoggedUserProfile({sessionUser}) {
  const user = useSelector(state => state.users);


  const [loading , setLoading] = useState(false);

  const dispatch = useDispatch();

  const {userId} = useParams();

  useEffect(() => {
    dispatch(userProfile(userId));
  }, [dispatch]);


  function sliceTimeOffDate(){
    let slicedDate = user.events.event_date.slice(0,10);
    return slicedDate;
  }


  // load the events the user is hosting and the events the user is attending so they can be displayed on the page separately
  // using user.events.hosting and user.events.attending
  function loadEvents(){
    if(user.events){
      return user.events.map(event => {
        return (
          <div className='event-container'>
            <div className='event-image'>
              <img src={event.image_url} alt='event'/>
            </div>
            <div className='event-info'>
              <div className='event-title'>
                <NavLink to={`/events/${event.id}`}>{event.title}</NavLink>
              </div>
              <div className='event-date'>
                {sliceTimeOffDate()}
              </div>
              <div className='event-location'>
                {event.location}
              </div>
            </div>
          </div>
        )
      })
    }
  }

  // load the events the user is attending




  return (
    <>

    </>
  )
}

export default LoggedUserProfile
