import React, { useState } from 'react';
import {useDispatch, useSelector} from 'react-redux'
import {useHistory} from 'react-router-dom'

import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';



function AddEvent() {
  const history = useHistory();
  const user = useSelector(state => state.session)

  const dispatch = useDispatch()

  let today = new Date().toISOString().substring(0, 10);

  const [show, setShow] = useState(false);
  const [event_imgs, setImage] = useState("");
  const [imageLoading, setImageLoading] = useState(false);
  const [title, setTitle] = useState("")
  const [event_description, setDescription] = useState("")
  const [event_date, setDate] = useState("")
  const [event_city, setCity] = useState("")
  const [event_state, setState] = useState("")
  const [event_zipcode, setZip] = useState("")
  const [category, setCategory] = useState("")
  const [errors, setErrors] = useState([])


  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>

      <i className="fas fa-upload" variant="primary" onClick={handleShow}>
      </i>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          I will not close if you click outside me. Don't even try to press
          escape key.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Understood</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default AddEvent;
